#include <iostream>#include <vector>#include <algorithm>using namespace std;void solveCase(int caseNum, vector<int> &nums) {    int n = nums.size();    vector<int> dp(n);    int maxSum = 0;        for (int i = 0; i < n; ++i) {        dp[i] = nums[i];        for (int j = 0; j < i; ++j) {            if (nums[j] < nums[i]) {                dp[i] = max(dp[i], dp[j] + nums[i]);            }        }        maxSum = max(maxSum, dp[i]);    }        cout << "case #" << caseNum << ":" << endl;    cout << maxSum << endl;}